import React, { useState, useRef, useEffect } from 'react';

export default function RotatingSphere() {
  const canvasRef = useRef(null);
  const [rotation, setRotation] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const previousMousePosition = useRef({ x: 0, y: 0 });

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    const width = canvas.width;
    const height = canvas.height;

    function drawSphere() {
      context.clearRect(0, 0, width, height);

      // Draw the sphere
      context.beginPath();
      context.arc(width / 2, height / 2, Math.min(width, height) / 2, 0, 2 * Math.PI);
      context.fillStyle = 'lightblue';
      context.fill();
    }

    function handleMouseDown(event) {
      setIsDragging(true);
      previousMousePosition.current = {
        x: event.clientX,
        y: event.clientY,
      };
    }

    function handleMouseUp() {
      setIsDragging(false);
    }

    function handleMouseMove(event) {
      if (!isDragging) return;

      const { clientX, clientY } = event;
      const delta = {
        x: clientX - previousMousePosition.current.x,
        y: clientY - previousMousePosition.current.y,
      };

      setRotation((prevRotation) => ({
        x: prevRotation.x + delta.y * 0.01,
        y: prevRotation.y + delta.x * 0.01,
      }));

      previousMousePosition.current = {
        x: clientX,
        y: clientY,
      };
    }

    function animate() {
      context.clearRect(0, 0, width, height);

      // Apply rotation transform
      context.translate(width / 2, height / 2);
      context.rotate(rotation.x);
      context.rotate(rotation.y);
      context.translate(-width / 2, -height / 2);

      drawSphere();

      requestAnimationFrame(animate);
    }

    drawSphere();
    animate();

    canvas.addEventListener('mousedown', handleMouseDown);
    canvas.addEventListener('mouseup', handleMouseUp);
    canvas.addEventListener('mousemove', handleMouseMove);

    return () => {
      canvas.removeEventListener('mousedown', handleMouseDown);
      canvas.removeEventListener('mouseup', handleMouseUp);
      canvas.removeEventListener('mousemove', handleMouseMove);
    };
  }, [isDragging, rotation]);

  return <canvas ref={canvasRef} width={400} height={400} />;
}
