import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';

export default function RotatingSphere() {
  const canvasRef = useRef(null);
  const previousMousePosition = useRef({ x: 0, y: 0 });
  const isDragging = useRef(false);
  const scale = useRef(1);

  useEffect(() => {
    const canvas = canvasRef.current;

    // Scene
    const scene = new THREE.Scene();

    // Set Size

    // Camera
    //const camera = new THREE.PerspectiveCamera(75, canvas.clientWidth / canvas.clientHeight, 0.1, 1000);
    const camera = new THREE.PerspectiveCamera(75, 800 / 800, 0.1, 1000);
    camera.position.z = 5;

    // Renderer
    const renderer = new THREE.WebGLRenderer({ canvas });
    //renderer.setSize(canvas.clientWidth, canvas.clientHeight);
    renderer.setSize(800, 800);

    // Geometry
    const geometry = new THREE.SphereGeometry(2, 32, 32);

    // Material
    const material = new THREE.MeshBasicMaterial({ color: 'white', wireframe: true });

    // Mesh
    const mesh = new THREE.Mesh(geometry, material);
    scene.add(mesh);

    // Mouse events
    function handleMouseDown(event) {
      isDragging.current = true;
      previousMousePosition.current = {
        x: event.clientX,
        y: event.clientY,
      };
    }

    function handleMouseUp(event) {
      isDragging.current = false;
    }

    function handleMouseMove(event) {
      if (!isDragging.current) return;

      const { clientX, clientY } = event;
      const delta = {
        x: clientX - previousMousePosition.current.x,
        y: clientY - previousMousePosition.current.y,
      };

      mesh.rotation.x += delta.y * 0.01;
      mesh.rotation.y += delta.x * 0.01;

      previousMousePosition.current = {
        x: clientX,
        y: clientY,
      };
    }

    function handleMouseWheel(event) {
      const delta = Math.sign(event.deltaY);

      scale.current += delta * 0.1;
      scale.current = Math.max(scale.current, 0.1); // 최소 스케일 제한

      mesh.scale.set(scale.current, scale.current, scale.current);
    }

    // Event listeners
    canvas.addEventListener('mousedown', handleMouseDown);
    canvas.addEventListener('mouseup', handleMouseUp);
    canvas.addEventListener('mousemove', handleMouseMove);
    canvas.addEventListener('wheel', handleMouseWheel);

    // Animation
    function animate() {
      requestAnimationFrame(animate);

      renderer.render(scene, camera);
    }

    animate();

    return () => {
      canvas.removeEventListener('mousedown', handleMouseDown);
      canvas.removeEventListener('mouseup', handleMouseUp);
      canvas.removeEventListener('mousemove', handleMouseMove);
      canvas.removeEventListener('wheel', handleMouseWheel);
    };
  }, []);

  return <canvas ref={canvasRef} width={400} height={400} />;
}
