import React, { useRef, useState } from 'react';
import { Canvas, useFrame } from 'react-three-fiber';
import { Sphere } from '@react-three/drei';

const RotatingSphere = () => {
  const sphereRef = useRef();
  const [isRotating, setRotating] = useState(false);

  // Mouse event handlers
  const handleMouseDown = () => setRotating(true);
  const handleMouseUp = () => setRotating(false);

  // Rotate the sphere based on mouse movement
  useFrame(({ mouse }) => {
    if (isRotating) {
      const sphere = sphereRef.current;
      sphere.rotation.x = mouse.y * Math.PI;
      sphere.rotation.y = mouse.x * Math.PI;
    }
  });

  return (
    <Sphere
      ref={sphereRef}
      args={[1, 32, 32]}
      onClick={handleMouseDown}
      onPointerUp={handleMouseUp}
    >
      <meshPhongMaterial attach="material" color="blue" />
    </Sphere>
  );
};

const App = () => {
  return (
    <Canvas style={{ height: '100vh' }}>
      <ambientLight />
      <pointLight position={[10, 10, 10]} />
      <RotatingSphere />
    </Canvas>
  );
};

export default App;
